import React, { useState } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

const Dashboard = (props) => {
  const [friend, setFriend] = useState("");
  let storedSession = sessionStorage.getItem("storedSession");

  return (
    <div>
      <h3>Live Chat Now With Your Friends</h3>
      <h3>Welcome {storedSession.user.username}</h3>
      <div>
        <input
          onChange={(event) => setFriend(event.target.value)}
          type="text"
          placeholder="type your friend's username"
        ></input>
        <Link
          onClick={(event) => (!friend ? event.preventDefault() : null)}
          to={`/Chat/${friend}`}
        >
          <button>Chat</button>
        </Link>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({
  user: state.Session,
});

export default connect(mapStateToProps)(Dashboard);
