import React, { useState } from "react";
import { connect } from "react-redux";
import Axios from "axios";
import "./Dashboard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSignOutAlt } from "@fortawesome/fontawesome-free-solid";

const Dashboard = (props) => {
  const [friend, setFriend] = useState("");
  let storedSession = sessionStorage.getItem("storedSession");

  const checkUser = (user) => {
    Axios.post(
      "/api/check",
      {
        username: user.toUpperCase(),
      },
      {
        withCredentials: true,
        crossdomain: true,
      }
    )
      .then((user) => {
        if (user.data.error) {
          alert(user.data.error);
        } else {
          {
            props.history.push(`/Chat/${user.data}`);
          }
          console.log(user.data);
        }
      })
      .catch((e) => {
        console.log(e);
      });

    console.log(user);
  };
  return (
    <div>
      <div className="logout">
        <h4>Sign Out</h4>
        <FontAwesomeIcon className="logout-button" icon={faSignOutAlt} />
      </div>
      <div className="welcome-message">
        {" "}
        <h3>Live Chat Now With Your Friends</h3>
        <h3>Welcome {storedSession}</h3>
      </div>

      <div className="new-user">
        <input
          className="friend-name"
          onChange={(event) => setFriend(event.target.value)}
          type="text"
          placeholder="type your friend's username"
        ></input>

        <button
          className="chat-button"
          onClick={(e) => {
            checkUser(friend);
          }}
        >
          Chat
        </button>
      </div>
      <div className="friends-dashboard">
        <div className="box">
          <img
            className="friendAvatar"
            src="https://www.pngitem.com/pimgs/m/78-786293_1240-x-1240-0-avatar-profile-icon-png.png"
            alt="Avatar"
          ></img>
          <div className="text">
            <p className="friendName">friends</p>
          </div>
        </div>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({
  user: state.Session,
});

export default connect(mapStateToProps)(Dashboard);
