import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import io from "socket.io-client";
let socket;
const Chat = (props) => {
  const [message, setMessage] = useState("");

  let socket = io();
  let friend = props.match.params.friend;
  let storedSession = sessionStorage.getItem("storedSession");

  let user = storedSession;
  console.log(user);
  console.log(friend);

  useEffect(() => {}, []);

  const sendMessage = (user, friend, message) => {};
  return (
    <div className="chat-page">
      <h2>Real Time Chat With Friends !</h2>
      <div className="chatWindow">
        <h4>{props.match.params.friend}</h4>
        <ul className="chat" id="chatList">
          <li className="self"></li>
          <li className="other"></li>
        </ul>
      </div>
      <div className="chatInputWrapper">
        <form>
          <input
            className="textarea input"
            type="text"
            placeholder="Enter your message..."
            onChange={setMessage(message)}
          />
          <input type="submit" onClick={setMessage()}></input>
        </form>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({});
const mapDispatchToProps = (dispatch) => ({});
export default connect(mapStateToProps, mapDispatchToProps)(Chat);
