import React from "react";
import { connect } from "react-redux";
import { RegisterAction } from "../../Redux/Actions";
import { useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import "./Register.css";

const SignUp = (props) => {
  let history = useHistory();
  const { handleSubmit, register, errors } = useForm();
  const onSubmit = (data) => {
    const userData = {
      username: data.username,
      password: data.password,
      gender: data.gender,
    };
    props.register(userData);
    setTimeout(2000, Redirect());
  };
  const Redirect = () => {
    if (props.session.user) {
      console.log(props.session);
      sessionStorage.setItem("storedSession", props.session.user.username);
      history.push("/dashboard");
    } else {
      console.log(props.session.error);
    }
  };
  return (
    <div className="register">
      <div className="register-left">
        <h1>Good to see you!</h1>
        <p>
          By creating an account on Guestbook, you agree to our Terms of use and
          Privacy Policy.
        </p>
      </div>
      <div className="register-form">
        <h3>Register</h3>
        <form onSubmit={handleSubmit(onSubmit)}>
          <label htmlFor="username"> Username </label>
          <input
            id="username"
            name="username"
            placeholder="Type your username"
            ref={register({ required: true, minLength: 3, maxLength: 10 })}
          />
          {errors.username && errors.username.type === "required" && (
            <p>This is required</p>
          )}
          {errors.username && errors.username.type === "minLength" && (
            <p>Minimum length is 3</p>
          )}
          {errors.username && errors.username.type === "maxLength" && (
            <p>Maximum length is 10</p>
          )}
          <label htmlFor="password">Password </label>
          <input
            id="password"
            name="password"
            placeholder="Type your password"
            type="password"
            ref={register({ required: true })}
          />
          {errors.password && errors.password.type === "required" && (
            <p>This is required</p>
          )}

          <label htmlFor="gender">Gender</label>
          <input name="gender" ref={register({ required: true })} list="list" />
          {errors.gender && <p>This is required</p>}
          <datalist id="list">
            <option value="Male" />
            <option value="Female" />
          </datalist>
          <input type="submit" ref={register({ required: true })} />
          <a> Already have an account </a>
        </form>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({
  session: state.Session,
});
const mapDispatchToProps = (dispatch) => ({
  register: (userData) => dispatch(RegisterAction(userData)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
