import React, { useState } from "react";
import { LoginAction } from "../../Redux/Actions";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

import { connect } from "react-redux";
const Login = (props) => {
  let history = useHistory();

  const { handleSubmit, register, errors } = useForm();

  const onSubmit = (data) => {
    const userData = {
      username: data.username,
      password: data.password,
    };
    props.login(userData);
    setTimeout(2000, Redirect());
  };
  const Redirect = () => {
    if (props.session.user) {
      console.log(props.session);
      sessionStorage.setItem("storedSession", props.session.user.username);
      history.push("/dashboard");
    } else {
      console.log(props.session.error);
    }
  };
  return (
    <div className="signin-form">
      <div className="left-signin">
        <div className="left-login">
          <h1>Good to see you again!</h1>
          <p>
            By logging into Guestbook, you agree to our Terms of use and Privacy
            Policy.
          </p>
        </div>
      </div>
      <div className="right-signin">
        <div className="right-info">
          <h3>Sign In</h3>
          <form onSubmit={handleSubmit(onSubmit)}>
            <label for="username"> Username </label>
            <input
              type="text"
              placeholder="Enter your username"
              name="username"
              id="username"
              ref={register({ required: true })}
            />
            {errors.username && errors.username.type === "required" && (
              <p>This is required</p>
            )}
            <label for="password">Password</label>
            <input
              id="password"
              placeholder="Enter your password"
              name="password"
              type="password"
              ref={register({ required: true })}
            />
            {errors.password && errors.password.type === "required" && (
              <p>This is required</p>
            )}
            <input type="submit" ref={register({ required: true })} />
            <a> Create an account </a>
          </form>
        </div>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({
  session: state.Session,
});
const mapDispatchToProps = (dispatch) => ({
  login: (user) => dispatch(LoginAction(user)),
});
export default connect(mapStateToProps, mapDispatchToProps)(Login);
