import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Axios from "axios";
import io from "socket.io-client";
import "./Chats.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPaperPlane } from "@fortawesome/fontawesome-free-solid";
let socket;
const Chat = (props) => {
  const [message, setMessage] = useState("");
  const [friends, setFriends] = useState([]);
  const [conversation, setConversation] = useState([]);

  let friend = props.match.params.friend;
  let storedSession = sessionStorage.getItem("storedSession");
  let user = storedSession;
  useEffect(() => {
    Axios.get("/friends", {
      username: user,
    })
      .then((res) => {
        setFriends(res.data);
      })
      .catch((e) => {
        console.log("Cant fetch friends ", e);
      });
    Axios.post("/chat", {
      username: user,
      friend: friend,
    })
      .then((res) => {
        setConversation(res.data);
      })
      .catch((e) => {
        console.log("Cant fetch Chat ", e);
      });
  }, [conversation]);

  console.log(conversation);

  const sendMessage = (messageData) => {
    Axios.post(
      "/messages",
      {
        username: messageData.user,
        friend: messageData.friend,
        message: messageData.message,
      },
      {
        withCredentials: true,
        crossdomain: true,
      }
    )
      .then((res) => {
        console.log("Response", res);
      })
      .catch((e) => {
        console.log("Cant Register ", e);
      });
    console.log(messageData);
  };
  return (
    <div className="chats-container">
      <div className="friends-list">
        {friends.map((friend, key) => {
          if (friend) {
            return (
              <div id={key} className="box">
                <img
                  className="friendAvatar"
                  src="https://www.pngitem.com/pimgs/m/78-786293_1240-x-1240-0-avatar-profile-icon-png.png"
                  alt="Avatar"
                ></img>
                <div className="text">
                  <p className="friendName">{friend}</p>
                </div>
              </div>
            );
          } else {
            return <p key={key}>You have no friends</p>;
          }
        })}
      </div>

      <div className="chat-window">
        <div className="chat-header">
          <img
            src="https://www.pngitem.com/pimgs/m/78-786293_1240-x-1240-0-avatar-profile-icon-png.png"
            alt="avatar"
          />
          <div className="friend-name">{friend}</div>
        </div>

        <div className="chat-history">
          <ul className="chat-ul-history">
            <li className="message-list">
              <div className="message-data align-right">
                <span className="message-data-time">10:10 AM, Today</span>
                <span className="message-data-name">Olia</span>
              </div>

              <div className="message other-message float-right">
                Hi Vincent, how are you? How is the project coming along?
              </div>
            </li>
            <li className="message-list">
              <div className="message-data align-right">
                <span className="message-data-time">10:10 AM, Today</span>
                <span className="message-data-name">Olia</span>
              </div>
              <div className="message other-message float-right">
                Hi Vincent, how are you? How is the project coming along?
              </div>
            </li>
            <li className="message-list">
              <div className="message-data align-right">
                <span className="message-data-time">10:10 AM, Today</span>
                <span className="message-data-name">Olia</span>
              </div>
              <div className="message other-message float-right">
                Hi Vincent, how are you? How is the project coming along?
              </div>
            </li>
            <li className="message-list">
              <div className="message-data align-right">
                <span className="message-data-time">10:10 AM, Today</span>
                <span className="message-data-name">Olia</span>
              </div>
              <div className="message other-message float-right">
                Hi Vincent, how are you? How is the project coming along?
              </div>
            </li>

            <li className="message-list">
              <div className="message-data">
                <span className="message-data-name">
                  <i className="fa fa-circle online"></i> Vincent
                </span>
                <span className="message-data-time">10:12 AM, Today</span>
              </div>
              <div className="message my-message">
                Are we meeting today? Project has been already finished and I
                have results to show you.
              </div>
            </li>

            <li className="message-list">
              <div className="message-data align-right">
                <span className="message-data-time">10:14 AM, Today</span>
                &nbsp;
                <span className="message-data-name">Olia</span>
                <i className="fa fa-circle me"></i>
              </div>
              <div className="message other-message float-right">
                Well I am not sure. The rest of the team is not here yet. Maybe
                in an hour or so? Have you faced any problems at the last phase
                of the project?
              </div>
            </li>

            <li className="message-list">
              <div className="message-data">
                <span className="message-data-name">
                  <i className="fa fa-circle online"></i> Vincent
                </span>
                <span className="message-data-time">10:20 AM, Today</span>
              </div>
              <div className="message my-message">
                Actually everything was fine. I'm very excited to show this to
                our team.
              </div>
            </li>
          </ul>
        </div>

        <div className="send-message">
          <textarea
            className="message-to-send"
            id="message-to-send"
            placeholder="Type your message"
            rows="3"
            onChange={(e) => {
              setMessage(e.target.value);
            }}
          ></textarea>
          <FontAwesomeIcon
            onClick={() => sendMessage({ user, friend, message })}
            className="send-button"
            icon={faPaperPlane}
          />
        </div>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({});
const mapDispatchToProps = (dispatch) => ({});
export default connect(mapStateToProps, mapDispatchToProps)(Chat);

