import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import io from "socket.io-client";
let socket;
const Chat = (props) => {
  const [friendChat, setFriendChat] = useState("");
  const [selfUser, setSelfUser] = useState("");
  setSelfUser("hazem-2");
  let socket = io();
  let ENDPOINT = "localhost:6000";
  useEffect(() => {
    setFriendChat(props.match.params.friend);
    console.log(friendChat);
    socket.on("connect", () => {
      console.log(socket.connected);
      socket.emit("join", friendChat);
    });
    socket.emit("join", { friendChat, selfUser }, () => {});
    return () => {
      socket.emit("disconnect");
      socket.off();
    };
    console.log(socket);
  }, [ENDPOINT]);
  return (
    <div className="chat-page">
      <h2>Real Time Chat With Friends !</h2>
      <div className="chatWindow">
        <h4>{props.match.params.friend}</h4>
        <ul className="chat" id="chatList">
          <li className="self"></li>
          <li className="other"></li>
        </ul>
      </div>
      <div className="chatInputWrapper">
        <form>
          <input
            className="textarea input"
            type="text"
            placeholder="Enter your message..."
          />
          <input type="submit"></input>
        </form>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({});
const mapDispatchToProps = (dispatch) => ({});
export default connect(mapStateToProps, mapDispatchToProps)(Chat);
