import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Axios from "axios";
import io from "socket.io-client";
import "./Chats.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPaperPlane } from "@fortawesome/fontawesome-free-solid";
let socket;
const Chat = (props) => {
  const [message, setMessage] = useState("");
  const [friends, setFriends] = useState([]);
  const [conversation, setConversation] = useState([]);
  const [date, setDate] = useState("");
  var d = new Date(date);
  d.toUTCString();

  let friend = props.match.params.friend;
  let storedSession = sessionStorage.getItem("storedSession");
  let user = storedSession;
  useEffect(() => {
    Axios.get("/friends", {
      username: user,
    })
      .then((res) => {
        setFriends(res.data);
      })
      .catch((e) => {
        console.log("Cant fetch friends ", e);
      });
    Axios.post("/chat", {
      username: user,
      friend: friend,
    })
      .then((res) => {
        setConversation(res.data);
      })
      .catch((e) => {
        console.log("Cant fetch Chat ", e);
      });
  }, []);

  console.log(conversation);

  const sendMessage = (messageData) => {
    Axios.post(
      "/messages",
      {
        username: messageData.user,
        friend: messageData.friend,
        message: messageData.message,
      },
      {
        withCredentials: true,
        crossdomain: true,
      }
    )
      .then((res) => {
        console.log("Response", res);
      })
      .catch((e) => {
        console.log("Cant Register ", e);
      });
    console.log(messageData);
  };
  return (
    <div className="chats-container">
      <div className="friends-list">
        {friends.map((friend, key) => {
          if (friend) {
            return (
              <div id={key} className="box">
                <img
                  className="friendAvatar"
                  src="https://www.pngitem.com/pimgs/m/78-786293_1240-x-1240-0-avatar-profile-icon-png.png"
                  alt="Avatar"
                ></img>
                <div className="text">
                  <p className="friendName">{friend}</p>
                </div>
              </div>
            );
          } else {
            return <p key={key}>You have no friends</p>;
          }
        })}
      </div>

      <div className="chat-window">
        <div className="chat-header">
          <img
            src="https://www.pngitem.com/pimgs/m/78-786293_1240-x-1240-0-avatar-profile-icon-png.png"
            alt="avatar"
          />
          <div className="friend-name">{friend}</div>
        </div>

        <div className="chat-history">
          <ul className="chat-ul-history">
            {conversation.map((message, key) => {
              setDate(message.dateCreated);
              return (
                <li className="message-list" key={key}>
                  {message.sender === user ? (
                    <>
                      <div className="message-data align-right">
                        <span className="message-data-time">{}</span>
                        <span className="message-data-name">
                          {message.sender}
                        </span>
                      </div>
                      <div className="message other-message float-right">
                        {message.message}
                      </div>
                    </>
                  ) : (
                    <>
                      <div className="message-data">
                        <span className="message-data-name">
                          <i className="fa fa-circle online"></i>
                          {message.sender}
                        </span>
                        <span className="message-data-time">{d}</span>
                      </div>
                      <div className="message my-message">
                        {message.message}
                      </div>
                    </>
                  )}
                </li>
              );
            })}
          </ul>
        </div>

        <div className="send-message">
          <textarea
            className="message-to-send"
            id="message-to-send"
            placeholder="Type your message"
            rows="3"
            onChange={(e) => {
              setMessage(e.target.value);
            }}
          ></textarea>
          <FontAwesomeIcon
            onClick={() => sendMessage({ user, friend, message })}
            className="send-button"
            icon={faPaperPlane}
          />
        </div>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => ({});
const mapDispatchToProps = (dispatch) => ({});
export default connect(mapStateToProps, mapDispatchToProps)(Chat);

